mapper.update:
UPDATE sys_menu
parent_id = #{parentId}, parent_ids = #{parentIds}, name = #{name}, href = #{href}, icon = #{icon}, sort = #{sort}, is_show = #{isShow}, permission = #{permission}, update_date = #{updateDate}, remarks = #{remarks}
WHERE id = #{id}

UPDATE sys_menu
SET
    parent_id  = #{parentId},
    parent_ids = #{parentIds}
WHERE id = #{id}
